mapper.select:
		select `chector_id`,username,`deleted`,hospital,section,`chector_name`,`doctor_num`,`sex`,`age`,`id_card`,`phone`,`email`,`headshot`,`add_time`,`update_time`,`remark` from t_chector
        where deleted=0  
		  		   and chector_id = #{chectorId} 
		  		   and deleted = #{deleted} 
		  		   and chector_name like concat('%', #{chectorName},'%')
		  		   and doctor_num = #{doctorNum} 
		  		   and sex = #{sex} 
		  		   and age = #{age} 
		  		   and id_card = #{idCard} 
		  		   and phone = #{phone} 
		  		   and email = #{email} 
		  		   and headshot = #{headshot} 
		  		   and add_time = #{addTime} 
		  		   and update_time = #{updateTime} 
		  		   and remark = #{remark} 



                order by ${sort} ${order}


                order by chector_id desc



			limit #{offset}, #{limit}

select count(*) from t_chector where deleted=0  

