mapper.select:
		select `t_eyesight_id`,`student_id`,`checkor_id`,`life_farvision_od`,`life_farvision_os`,`naked_farvision_od`,`naked_farvision_os`,`correction_farvision_od`,`correction_farvision_os`,`life_nearvision_od`,`life_nearvision_os`,`naked_nearvision_od`,`naked_nearvision_os`,`check_date`,date_format(t_result_eyesight.check_date, '%Y-%m-%d') as checkDate1,`delete_flag`,`identity_card` from t_result_eyesight

		  		   and t_eyesight_id = #{tEyesightId} 
		  		   and student_id = #{studentId} 
		  		   and checkor_id = #{checkorId} 
		  		   and life_farvision_od = #{lifeFarvisionOd} 
		  		   and life_farvision_os = #{lifeFarvisionOs} 
		  		   and naked_farvision_od = #{nakedFarvisionOd} 
		  		   and naked_farvision_os = #{nakedFarvisionOs} 
		  		   and correction_farvision_od = #{correctionFarvisionOd} 
		  		   and correction_farvision_os = #{correctionFarvisionOs} 
		  		   and life_nearvision_od = #{lifeNearvisionOd} 
		  		   and life_nearvision_os = #{lifeNearvisionOs} 
		  		   and naked_nearvision_od = #{nakedNearvisionOd} 
		  		   and naked_nearvision_os = #{nakedNearvisionOs} 
		  		   and check_date = #{checkDate} 
		  		   and delete_flag = #{deleteFlag} 
		  		   and identity_card = #{identityCard} 





                order by ${sort} ${order}


                order by t_eyesight_id desc



			limit #{offset}, #{limit}

select count(*) from t_result_eyesight

  		   and t_eyesight_id = #{tEyesightId} 
  		   and student_id = #{studentId} 
  		   and checkor_id = #{checkorId} 
  		   and life_farvision_od = #{lifeFarvisionOd} 
  		   and life_farvision_os = #{lifeFarvisionOs} 
  		   and naked_farvision_od = #{nakedFarvisionOd} 
  		   and naked_farvision_os = #{nakedFarvisionOs} 
  		   and correction_farvision_od = #{correctionFarvisionOd} 
  		   and correction_farvision_os = #{correctionFarvisionOs} 
  		   and life_nearvision_od = #{lifeNearvisionOd} 
  		   and life_nearvision_os = #{lifeNearvisionOs} 
  		   and naked_nearvision_od = #{nakedNearvisionOd} 
  		   and naked_nearvision_os = #{nakedNearvisionOs} 
  		   and check_date = #{checkDate} 
  		   and delete_flag = #{deleteFlag} 
  		   and identity_card = #{identityCard}

select *, LEAST(tre.naked_farvision_od,tre.naked_farvision_os) dushu from t_result_eyesight tre , t_student ts
	where tre.identity_card = ts.identity_card 
	and ts.school = #{school} and tre.check_date between #{start} and #{end}
	 and ts.grade = #{grade} 
	group by tre.identity_card

select *, LEAST(tre.naked_farvision_od,tre.naked_farvision_os) dushu from t_result_eyesight tre , t_student ts
	where tre.identity_card = ts.identity_card
	and ts.school = #{school}
	 and date_format(tre.check_date,'%Y') = #{checkDate} 
	 and ts.student_sex = #{studentSex} 
	 and ts.grade = #{grade} 
	GROUP BY  tre.identity_card

select *, LEAST(tre.naked_farvision_od,tre.naked_farvision_os) dushu from t_result_eyesight tre , t_student ts
	where tre.identity_card = ts.identity_card 
	and ts.school = #{school} and tre.check_date between #{start} and #{end}
	 and ts.student_sex = #{studentSex} 
	 and ts.grade = #{grade} 
	 and ts.student_class = #{studentClass} 
	 and tre.identity_card = #{identityCard} 
	group by tre.identity_card

select ifnull(`correction_farvision_od`,'') correctionFarvisionOd,ifnull(`correction_farvision_os`,'') correctionFarvisionOs
,ifnull(`naked_farvision_od`,'') nakedFarvisionOd,ifnull(`naked_farvision_os`,'') nakedFarvisionOs
 from t_result_eyesight where identity_card = #{identityCard}
and check_date between #{start} and #{end}

select tre.identity_card, LEAST(tre.naked_farvision_od,tre.naked_farvision_os) dushu from t_result_eyesight tre 
	where tre.identity_card = #{identityCard} and tre.check_date = #{checkDate}
	and tre.naked_farvision_od is not null and tre.naked_farvision_os is not null

select tre.identity_card, LEAST(tre.naked_farvision_od,tre.naked_farvision_os) dushu from t_result_eyesight tre , t_student ts
	where tre.identity_card = ts.identity_card and tre.check_date = #{checkDate}
	and ts.xue_bu = #{xueBu}
	group by tre.identity_card

select tre.identity_card, LEAST(tre.naked_farvision_od,tre.naked_farvision_os) dushu from t_result_eyesight tre , t_student ts
	where tre.identity_card = ts.identity_card and tre.check_date = #{checkDate}
	and ts.student_sex = #{studentSex}
	group by tre.identity_card
