mapper.select:
		select `t_visibility_id`,`student_id`,`checkor_id`,`stereoscopic_viewing_value`,`stereoscopic_viewing_dis`,`adjustment_range_od`,`adjustment_range_oc`,`adjustment_range_ou`,`gather_near_od`,`gather_near_oc`,`gather_near_ou`,`oblique_value`,`oblique_dis`,`before_after_od_value`,`before_after_od_dis`,`before_after_os_value`,`before_after_os_dis`,`check_date`,`delete_flag`,`identity_card` from t_result_visibility

		  		   and t_visibility_id = #{tVisibilityId} 
		  		   and student_id = #{studentId} 
		  		   and checkor_id = #{checkorId} 
		  		   and stereoscopic_viewing_value = #{stereoscopicViewingValue} 
		  		   and stereoscopic_viewing_dis = #{stereoscopicViewingDis} 
		  		   and adjustment_range_od = #{adjustmentRangeOd} 
		  		   and adjustment_range_oc = #{adjustmentRangeOc} 
		  		   and adjustment_range_ou = #{adjustmentRangeOu} 
		  		   and gather_near_od = #{gatherNearOd} 
		  		   and gather_near_oc = #{gatherNearOc} 
		  		   and gather_near_ou = #{gatherNearOu} 
		  		   and oblique_value = #{obliqueValue} 
		  		   and oblique_dis = #{obliqueDis} 
		  		   and before_after_od_value = #{beforeAfterOdValue} 
		  		   and before_after_od_dis = #{beforeAfterOdDis} 
		  		   and before_after_os_value = #{beforeAfterOsValue} 
		  		   and before_after_os_dis = #{beforeAfterOsDis} 
		  		   and check_date = #{checkDate} 
		  		   and delete_flag = #{deleteFlag} 
		  		   and identity_card = #{identityCard} 





                order by ${sort} ${order}


                order by t_visibility_id desc



			limit #{offset}, #{limit}

