mapper.select:
		select `t_diopter_id`,`t_optometry_id`,`diopter_s`,`diopter_c`,`diopter_a`,`believe`,`num`,`type`,`ifRL`,first_second,activity_id from t_result_diopter_new

		  		   and t_diopter_id = #{tDiopterId} 
		  		   and t_optometry_id = #{tOptometryId} 
		  		   and diopter_s = #{diopterS} 
		  		   and diopter_c = #{diopterC} 
		  		   and diopter_a = #{diopterA} 
		  		   and believe = #{believe} 
		  		   and num = #{num} 
		  		   and type = #{type} 
		  		   and ifRL = #{ifrl} 
		  		   and first_second = #{firstSecond} 
		  		   and check_date = #{checkDate} 
		  		   and activity_id = #{activityId} 



                order by ${sort} ${order}


                order by t_diopter_id desc



			limit #{offset}, #{limit}

select count(*) from t_result_diopter_new

  		   and t_diopter_id = #{tDiopterId} 
  		   and t_optometry_id = #{tOptometryId} 
  		   and diopter_s = #{diopterS} 
  		   and diopter_c = #{diopterC} 
  		   and diopter_a = #{diopterA} 
  		   and believe = #{believe} 
  		   and num = #{num} 
  		   and type = #{type} 
  		   and ifRL = #{ifrl} 
  		   and first_second = #{firstSecond} 
  		   and activity_id = #{activityId}

SELECT trd.t_diopter_id,trd.identity_card,MIN(trd.dengxiaoqiujing) dengxiaoqiujing FROM t_result_diopter_new trd,t_student ts
 where trd.identity_card = ts.identity_card
and trd.type = 'AVG'
 and ts.school = #{school} 
 and ts.student_sex = #{studentSex} 
 and date_format(trd.check_date,'%Y') = #{checkDate} 
 and ts.grade = #{grade} 
 and ts.student_class = #{studentClass} 
 and trd.identity_card = #{identityCard}

SELECT trd.t_diopter_id,trd.identity_card,MIN(trd.dengxiaoqiujing) dengxiaoqiujing FROM t_result_diopter_new trd
 where trd.identity_card
and trd.type = 'AVG' and trd.check_date between #{start} and #{end} and trd.identity_card = #{identityCard}
