		  		   and id = #{id} 
		  		   and check_type = #{checkType} 
		  		   and student_name like concat('%', #{studentName},'%') 
		  		   and student_sex = #{studentSex} 
		  		   and nation = #{nation} 
		  		   and birthday = #{birthday} 
