		  		   and school = #{school} 
		  		   and grade = #{grade} 
		  		   and student_class = #{studentClass} 
		  		   and phone = #{phone} 
		  		   and address = #{address} 
		  		   and height = #{height} 
		  		   and weight = #{weight} 
