select count(*) from t_student_new 
where last_check_time is not null
	 and  check_type = #{checkType} 
	 and  address = #{address} 
	  and DATE_FORMAT(last_check_time,'%Y-%m-%d') between '2020-06-01' and '2020-12-31'

select count(*) from t_student_new
where last_check_time is not null
and  check_type = #{checkType} 
and  address = #{address} 
and last_check_time > DATE_FORMAT('2021-01-00','%Y-%m-%d')

