mapper.select:
select ts.id ,ts.student_class,ts.school,ts.student_name,ts.student_sex,ts.identity_card,ts.phone,ts.birthday from t_student_new ts 
where ts.grade = #{grade} and ts.school = #{school} and  ts.activity_id = #{activityId}
and ts.student_class = #{studentClass}

		select activity_id,school,grade from t_student_new where activity_id = #{activityId} and school = #{school} 
		  and sys_id = #{sysId} 
 		GROUP BY grade
		ORDER BY field(grade,'幼儿园','一年级','二年级','三年级','四年级','五年级','六年级','初一','初二','初三','初四','高一','高二','高三')

		select activity_id,school,grade,student_class from t_student_new where activity_id = #{activityId} and school = #{school} and grade = #{grade}
		  and sys_id = #{sysId} 
 		GROUP BY student_class

